ScrollReveal({ duration: 1000 })

ScrollReveal().reveal
('.animate-top',{ 
        
    opacity: '0',
    distance: '60px',
    origin: 'top',
    reset: false,
    interval: 200  

});


ScrollReveal().reveal
('.animate-fade',{ 
    
    opacity: '0',
    distance: '60px',
    scale: '0.5',
    reset: false,
    interval: 200,
    delay: 300

});


ScrollReveal().reveal
('.animate-noti-top',{ 
        
    opacity: '0',
    distance: '100px',
    origin: 'top',
    reset: false,
    delay: 5000,
    interval: 200  

});
